<?php session_start();$id = $_POST['id'];$id = str_replace('-','#',$id);$id = str_replace('_',' ',$id);$qtd = $_POST['qtd'];$carrinho = $_SESSION['ts_carrinho'];if ($qtd != 0) {    $carrinho[$id] = $qtd;} else {    unset($carrinho[$id]);}$_SESSION['ts_carrinho'] = $carrinho;$count = count($carrinho);$msg = '';if ($count == 0) {    $msg = 'Nenhum produto adicionado';    unset($_SESSION['ts_carrinho']);} elseif ($count == 1) {    $msg = '1 produto adicionado';} else {    $msg = $count.' produtos adicionados';}echo $msg;//unset($_SESSION['ts_carrinho']);?>