<?phpsession_start();include 'configuracoes.php';include 'conectar.php';include 'funcoes.php';include 'arrays.php';$produtos = '';$prodq = 0;@$carrinho = $_SESSION['ts_carrinho'];$queryint = "SELECT * FROM ft_produtos ORDER by slug" or die("Erro.." . mysqli_error($link_db));$queryint = mysqli_query($link_db, $queryint);while ($produto = mysqli_fetch_array($queryint)) {    $prodid = $produto['id'];    if ($produto['variacao']) {        $vararray = explode('#', $produto['variacao']);        if ($produto['tipodevariacao'] == 'texto') {            foreach ($vararray as &$opcao) {                @$isset = $carrinho[$produto['id'].'#'.$opcao];                if ($isset) {                    $produtos .= '<p><b>'.$produto['nome'].' - '.$opcao.':</b>  '.$carrinho[$prodid.'#'.$opcao].'</p>';                }            }        }        else        {            $firstnum = intval($vararray[0]);            $lastnum = intval($vararray[1]);            for ( $i = $firstnum; $i <= $lastnum ; $i++ ) {                @$isset = $carrinho[$produto['id'].'#'.$i];                if ($isset) {                    $produtos .= '<p><b>' . $produto['nome'] . ' - Nº ' . $i . ':</b>  ' . $carrinho[$prodid . '#' . $i] . '</p>';                }            }        }    }else{    @$isset = $carrinho[$produto['id'].'#'];    if ($isset) {        $prodid = $produto['id'];        $produtos .= '<p><b>'.$produto['nome'].':</b>  '.$carrinho[$prodid.'#'].'</p>';         }}}$dados = array();$usuario = $_SESSION['ts_client_id'];$queryint = "SELECT * FROM ft_usuario ORDER by slug" or die("Erro.." . mysqli_error($link_db));$queryint = mysqli_query($link_db, $queryint);while ($user = mysqli_fetch_array($queryint)) {    $dados = $user;};if ($dados['complemento']){    $dados['complemento'] = ' - '. $dados['complemento'];}$mensagem = '<h1>Pedido de Orçamento</h1>';$mensagem .= '<h2>Produtos</h2>';$mensagem .= $produtos;$mensagem .= '<h2>Solicitante</h2>';$mensagem .= '<p><b>Empresa: </b><br>'.$dados['razao'].'</p>';$mensagem .= '<p><b>Responsável:</b><br>'.$dados['responsavel'].'</p>';$mensagem .= '<p><b>CNPJ:</b><br>'.$dados['cnpj'].'</p>';$mensagem .= '<h3>Contato</h3>';$mensagem .= '<p><b>Telefone: </b><br>'.$dados['telefone'].'</p>';$mensagem .= '<p><b>Email: </b><br>'.$dados['email'].'</p>';$mensagem .= '<p><b>Endereço: </b><br>'.$dados['logradouro'].', '.$dados['numero'].$dados['complemento'].', '.$dados['bairro'].' - '.$dados['cidade'].'-'.$dados['estado'].'<br>'.$dados['cep'].'</p>';$slug = $dados['slug'];$sql = "INSERT INTO ft_orcamentos (               userslug,               produtos,               datahora            )            VALUES            (               '{$slug}',               '{$produtos}',               now()            )" or die("Erro.." . mysqli_error($link_db));$result = mysqli_query($link_db, $sql);unset($_SESSION['ts_carrinho']);$from     = $dados['email'];$fromName = $dados['razao'];// Os arquivos do PHPMailer foram extraidos na pasta /mailrequire 'phpmailer/class.phpmailer.php';require 'phpmailer/class.smtp.php';$mail = new PHPMailer();$mail->setLanguage('pt');//Variaveis de configuração do servidor do GMAIL$host     = $config['smtp_host'];$username = $config['smtp_user'];$password = $config['smtp_pass'];$port     = intval($config['smtp_port']);$secure   = 'tls';$mail->isSMTP();$mail->Host = $host;$mail->SMTPAuth   = true;$mail->Username   = $username;$mail->Password   = $password;$mail->Port       = $port;$mail->SMTPSecure = $secure;$mail->From       = $from;$mail->FromName   = $fromName;$mail->addReplyTo($from, $fromName);$mail->addAddress($config['email'], $config['titulo']);$mail->isHTML(true);$mail->CharSet     = 'utf-8';$mail->WordWrap    = 70;// Exemplos de texto para o e-mail com HTML e sem.$mail->Subject     = 'Solicitação de Orçamento';$mail->Body        = $mensagem;$mail->AltBody      = $mensagem;// Faz a validação se a mensagem foi enviada para o servidor.$send = $mail->Send();echo '<h2 class="text-center">';if($send)    echo 'Solicitação de orçamento enviada com sucesso!';else    echo $mail->ErrorInfo;echo '</h2>';