<?php session_start();include 'configuracoes.php';include 'conectar.php';@$userid = $_SESSION['ts_client_id'];if (!$userid) {    echo 'false';} else {    $id = $_POST['id'];    @$variacao = $_POST['variacao'];    $newvar = '#'.$variacao;    @$carrinho = $_SESSION['ts_carrinho'];    if (!$carrinho) {        $_SESSION['ts_carrinho'] = array();    }    $carrinho = $_SESSION['ts_carrinho'];    $qtd = 0;    @$idcarrinho = $carrinho[$id . $newvar];    if ($idcarrinho) {        $qtd = $carrinho[$id . $newvar];    }    $queryint = "SELECT * FROM ft_produtos WHERE id = {$id}" or die("Erro.." . mysqli_error($link_db));    $queryint = mysqli_query($link_db, $queryint);    while ($produto = mysqli_fetch_array($queryint)) {        $prod = $produto;    }    $varcheck = '';    if (!$prod['variacao']) {        $varcheck = 'existe';    }    if ($variacao) {        $varcheck = 'existe';    }    if ($varcheck) {        ?>        <form id="formaddprod" onsubmit="addcarrinho(baseurl); return false;">            <h4><?php echo $prod['nome']; if ($variacao){echo ' '.$variacao;} ?></h4>            <div class="row">                <div class="col-xs-12 col-sm-6 bottom-30">                    <input class="form-control input-lg" required type="number" id="qtdprod" min="0"                           value="<?php echo $qtd; ?>">                </div>                <div class="col-xs-12 col-sm-6">                    <input type="hidden" id="idprod" value="<?php echo $id . '#' . $variacao; ?>">                    <button class="btn btn-block btn-form btn-lg">Adicionar Produto</button>                </div>        </form>        <?php    } else {        ?>        <h5>Opções de <?php if ($prod['tipodevariacao'] == 'texto') {?>produto<?php } else { ?>tamanho<?php } ?>:</h5>            <?php            $vararray = explode('#',$prod['variacao']); ?>        <div class="row">        <div class="col-xs-12 col-sm-6 bottom-30">        <select class="form-control input-lg" id="variacoes">        <?php            if ($prod['tipodevariacao'] == 'texto')            {                foreach ($vararray as &$opcao) {                    ?>                    <option value="<?php echo $opcao; ?>"><?php echo $opcao; ?></option>                    <?php                }            }            else            {                $firstnum = intval($vararray[0]);                $lastnum = intval($vararray[1]);                for ( $i = $firstnum; $i <= $lastnum ; $i++ ) {                    ?><option value="<?php echo $i; ?>"><?php echo $i; ?></option><?php                }            }            ?>        </select>        </div>        <div class="col-xs-12 col-sm-6">            <button onclick="varselect('<?php echo $_POST['id']; ?>', '<?php echo $baseurl; ?>');" class="btn btn-block btn-form btn-lg">Selecionar</button>            </div>        </div>    <?php }}?>